import React, { useState } from "react";
import styled from "styled-components";
const Card = ({ data, deleteCard, editItem }) => {
  const [isEditing, setIsEditing] = useState(false);
  const { item, calorie } = data;
  const [i, setI] = useState(item);
  const [c, setC] = useState(calorie);
  const handleSubmit = (e) => {
    e.preventDefault();
    editItem({ i, c });
    setIsEditing(false);
  };
  if (isEditing) {
    return (
      <>
        <input
          type="text"
          placeholder="Item"
          value={i}
          required
          onChange={(e) => {
            setI(e.target.value);
          }}
        />
        <input
          type="number"
          placeholder="calorie"
          value={c}
          required
          onChange={(e) => {
            setC(e.target.value);
          }}
        />
        <button
          onClick={(e) => {
            handleSubmit(e);
          }}
        >
          Done
        </button>
        <button
          onClick={() => {
            deleteCard(item);
          }}
        >
          Delete
        </button>
      </>
    );
  }
  return (
    <StyledCard>
      <h1>{item}</h1>
      <h3>you have consumed {calorie} calories</h3>
      <Buttons>
        <Delete
          onClick={() => {
            deleteCard(item);
          }}
        >
          Remove
        </Delete>

        <Edit
          onClick={() => {
            setIsEditing(true);
          }}
        >
          Edit
        </Edit>
      </Buttons>
    </StyledCard>
  );
};

export default Card;

const StyledCard = styled.div`
  border: 2px solid #39f;
  padding: 20px;
  margin-bottom: 10px;
`;

const Buttons = styled.div`
  padding: 10px;
  button {
    margin: 10px;
    cursor: pointer;
    padding: 10px;
    border: none;
    color: #fff;
    font-size: 20x;
    border-radius: 4px;
  }
`;

const Delete = styled.button`
  background-color: #fd6e0d;
`;

const Edit = styled.button`
  background-color: #0d6efd;
`;
